registerPaint(
  'circle-nav',
  class {
    static get inputProperties() {
      return ['--circle-color', '--item-size', '--angle', '--offset'];
    }
    paint(ctx, geom, properties) {
      // Change the fill color.
      const circle = properties.get('--circle-color');
      const offset = ((properties.get('--angle').value - 90) * Math.PI) / 180;
      const angle = properties.get('--offset');
      const θ = ((angle - 90) * Math.PI) / 180;
      const p = properties.get('--item-size').value;

      // Determine the center point and radius.
      const xCircle = geom.width / 2;
      const yCircle = geom.height / 2;
      const radiusCircle = Math.min(xCircle, yCircle);

      // Draw the circle \o/
      ctx.beginPath();
      ctx.arc(xCircle, yCircle, radiusCircle, offset, θ);
      ctx.strokeStyle = circle;
      // ctx.fillStyle = circle;
      // ctx.fill();
      ctx.stroke();

      ctx.beginPath();
      ctx.arc(xCircle, yCircle, radiusCircle - p - 10, offset, θ);
      ctx.strokeStyle = circle;
      // ctx.fillStyle = circle;
      // ctx.fill();
      ctx.stroke();
    }
  }
);
